<?phprequire_once('functions/connect.php');try {		$taskStatus = 0;	if($_POST['taskStage'] == 'incomplete') {		$taskStatus = 0;	} else if($_POST['taskStage'] == 'pending') {		$taskStatus = 1;	} else if($_POST['taskStage'] == 'complete') {		$taskStatus = 2;	} else if($_POST['taskStage'] == 'na') {		$taskStatus = 3;	}	$currentTime = date("Y-m-d : H:i:s", time());		$updateTaskStatement = "UPDATE sitetask SET taskStage = ?, updateTime = ? WHERE id = ?";	$updateTaskPrep = $dbh->prepare($updateTaskStatement);	$updateTaskPrep->execute(array($taskStatus, $currentTime, $_POST['taskId']));	echo '{"success": true, "msg":'.json_encode('Task Updated') . '}';	} catch (Exception $e) {	echo '{"failure": true, "msg":'.json_encode('Error Updating') . '}';	}?>